import React from 'react';
import { Link } from 'react-router-dom';

const Header = () => (
  <header style={{
    background: '#222',
    color: '#fff',
    padding: '18px 0',
    marginBottom: 30,
    boxShadow: '0 2px 8px #eee'
  }}>
    <div style={{
      maxWidth: 1200,
      margin: '0 auto',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '0 24px'
    }}>
      <div>
        <h1 style={{ margin: 0, fontSize: 32, letterSpacing: 2 }}>Shri Shyam Bhojnalaya</h1>
        <div style={{ fontSize: 14, color: '#ffd700', marginTop: 4 }}>
          Contact: <a href="tel:+919999999999" style={{ color: '#ffd700', textDecoration: 'underline' }}>+91-9999999999</a> | 
          Email: <a href="mailto:info@shrishyam.com" style={{ color: '#ffd700', textDecoration: 'underline' }}>info@shrishyam.com</a>
        </div>
      </div>
      <nav>
        <Link to="/" style={navLink}>Home</Link>
        <Link to="/cart" style={navLink}>Cart</Link>
        <Link to="/admin-login" style={navLink}>Admin</Link>
      </nav>
    </div>
  </header>
);

const navLink = {
  color: '#fff',
  marginLeft: 24,
  textDecoration: 'none',
  fontWeight: 'bold',
  fontSize: 16,
  transition: 'color 0.2s'
};

export default Header;
