import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../CartContext';
import { ShoppingCart, ClipboardList, UtensilsCrossed } from 'lucide-react'; // Using icons for a premium feel

const Dashboard = () => {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [activeCategory, setActiveCategory] = useState('All');
  const [loading, setLoading] = useState(true);
  const [addMsg, setAddMsg] = useState('');
  const { cart, addToCart, removeFromCart } = useCart();
  const navigate = useNavigate();

  const API_BASE_URL = "http://localhost:5000"; 

  useEffect(() => {
    const fetchData = async () => {
      try {
        const foodRes = await axios.get(`${API_BASE_URL}/api/foods`);
        const catRes = await axios.get(`${API_BASE_URL}/api/foods/categories`);
        setFoods(foodRes.data);
        setCategories(catRes.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching data:", err);
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    if (addMsg) {
      const timer = setTimeout(() => setAddMsg(''), 2000);
      return () => clearTimeout(timer);
    }
  }, [addMsg]);

  const filteredFoods = activeCategory === 'All' 
    ? foods 
    : foods.filter(item => item.category?.name === activeCategory);

  const isInCart = (id) => cart.some(item => item._id === id);

  if (loading) return <div style={loaderStyle}>Loading Deliciousness...</div>;

  return (
    <div style={containerStyle}>
      
      {/* --- FLOATING MODERN NAVBAR --- */}
      <nav style={navStyle}>
        <div style={navContent}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <UtensilsCrossed size={28} color="#e67e22" />
            <h2 style={{ margin: 0, letterSpacing: '-1px' }}>SKY BITES</h2>
          </div>
          
          <div style={{ display: 'flex', gap: '20px', alignItems: 'center' }}>
            <Link to="/order" style={navLinkStyle}>
              <ClipboardList size={20} />
              <span>Orders</span>
            </Link>
            <Link to="/cart" style={cartLinkStyle}>
              <ShoppingCart size={20} />
              <span>Cart</span>
              {cart.length > 0 && <span style={badgeStyle}>{cart.length}</span>}
            </Link>
          </div>
        </div>
      </nav>

      {/* --- HERO HEADER --- */}
      <header style={heroStyle}>
        <h1 style={{ fontSize: '3rem', margin: '0 0 10px 0', fontWeight: '900' }}>Taste the Extraordinary</h1>
        <p style={{ fontSize: '1.1rem', opacity: 0.8 }}>Handcrafted meals delivered straight to your doorstep.</p>
      </header>

      {/* --- GLOBAL SUCCESS MESSAGE --- */}
      {addMsg && <div style={toastStyle}>{addMsg}</div>}

      {/* --- CATEGORY SECTION --- */}
      <div style={categoryContainer}>
        <button 
          onClick={() => setActiveCategory('All')}
          style={catButtonStyle(activeCategory === 'All')}
        >
          üç¥ All Menu
        </button>
        {categories.map(cat => (
          <button 
            key={cat._id} 
            onClick={() => setActiveCategory(cat.name)}
            style={catButtonStyle(activeCategory === cat.name)}
          >
            {cat.name}
          </button>
        ))}
      </div>

      {/* --- FOOD GRID --- */}
      <main style={gridStyle}>
        {filteredFoods.map(item => (
          <div key={item._id} style={cardStyle}>
            <div onClick={() => navigate(`/item/${item._id}`)} style={{ cursor: 'pointer', position: 'relative' }}>
              <img 
                src={item.image.startsWith('http') ? item.image : `${API_BASE_URL}/${item.image}`} 
                alt={item.name}
                onError={(e) => { e.target.src = 'https://via.placeholder.com/400x300?text=Delicious+Food'; }}
                style={imageStyle} 
              />
              <div style={priceBadge}>${item.price}</div>
            </div>

            <div style={{ padding: '20px', flex: 1 }}>
              <h3 style={{ margin: '0 0 8px 0', fontSize: '1.4rem' }}>{item.name}</h3>
              <p style={descStyle}>{item.description || "Freshly prepared with the finest ingredients available today."}</p>
            </div>

            <div style={{ padding: '0 20px 20px 20px' }}>
              {isInCart(item._id) ? (
                <button onClick={() => removeFromCart(item._id)} style={removeBtnStyle}>
                  Remove from Order
                </button>
              ) : (
                <button onClick={() => {
                  addToCart(item);
                  setAddMsg(`Added ${item.name}!`);
                }} style={addBtnStyle}>
                  Add to Cart
                </button>
              )}
            </div>
          </div>
        ))}
      </main>

      {/* --- MINIMAL FOOTER --- */}
      <footer style={footerStyle}>
        <p>¬© 2026 Sky Bites. All rights reserved.</p>
        <Link to="/admin-panel" style={adminLinkStyle}>Secret Access</Link>
      </footer>
    </div>
  );
};

// --- MODERN STYLING OBJECTS ---

const containerStyle = {
  backgroundColor: '#f4f7f6',
  minHeight: '100vh',
  fontFamily: '"Inter", sans-serif',
  color: '#2d3436'
};

const navStyle = {
  position: 'sticky',
  top: 0,
  zIndex: 1000,
  backgroundColor: 'rgba(255, 255, 255, 0.95)',
  backdropFilter: 'blur(10px)',
  boxShadow: '0 2px 15px rgba(0,0,0,0.05)',
  padding: '15px 0'
};

const navContent = {
  maxWidth: '1200px',
  margin: '0 auto',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '0 20px'
};

const navLinkStyle = {
  display: 'flex',
  alignItems: 'center',
  gap: '6px',
  textDecoration: 'none',
  color: '#2d3436',
  fontWeight: '600',
  fontSize: '15px'
};

const cartLinkStyle = {
  ...navLinkStyle,
  backgroundColor: '#000',
  color: '#fff',
  padding: '8px 18px',
  borderRadius: '50px',
  position: 'relative'
};

const badgeStyle = {
  position: 'absolute',
  top: '-5px',
  right: '-5px',
  backgroundColor: '#e67e22',
  color: 'white',
  fontSize: '10px',
  padding: '2px 6px',
  borderRadius: '50%',
  border: '2px solid #fff'
};

const heroStyle = {
  textAlign: 'center',
  padding: '60px 20px',
  background: 'linear-gradient(135deg, #fff 0%, #f4f7f6 100%)'
};

const categoryContainer = {
  display: 'flex',
  gap: '12px',
  overflowX: 'auto',
  padding: '0 20px 30px',
  justifyContent: 'center',
  scrollbarWidth: 'none'
};

const catButtonStyle = (isActive) => ({
  padding: '12px 24px',
  borderRadius: '12px',
  border: 'none',
  backgroundColor: isActive ? '#e67e22' : '#fff',
  color: isActive ? '#fff' : '#2d3436',
  cursor: 'pointer',
  fontWeight: '700',
  fontSize: '14px',
  boxShadow: '0 4px 6px rgba(0,0,0,0.03)',
  transition: 'all 0.3s ease',
  whiteSpace: 'nowrap'
});

const gridStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
  gap: '30px',
  maxWidth: '1200px',
  margin: '0 auto',
  padding: '0 20px'
};

const cardStyle = {
  backgroundColor: '#fff',
  borderRadius: '24px',
  overflow: 'hidden',
  boxShadow: '0 10px 30px rgba(0,0,0,0.05)',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.3s ease'
};

const imageStyle = { width: '100%', height: '240px', objectFit: 'cover' };

const priceBadge = {
  position: 'absolute',
  bottom: '15px',
  right: '15px',
  backgroundColor: '#fff',
  padding: '6px 12px',
  borderRadius: '10px',
  fontWeight: '800',
  fontSize: '1.1rem',
