import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../CartContext'; // Ensure this path matches your folder structure
import { ShoppingCart, ClipboardList, UtensilsCrossed } from 'lucide-react';

const Dashboard = () => {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [activeCategory, setActiveCategory] = useState('All');
  const [loading, setLoading] = useState(true);
  const [addMsg, setAddMsg] = useState('');
  
  // ðŸ“± MOBILE DETECTION STATE
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  const { cart, addToCart, removeFromCart } = useCart();
  const navigate = useNavigate();
  const API_BASE_URL = "http://localhost:5000"; 

  // Detect Screen Resize
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const foodRes = await axios.get(`${API_BASE_URL}/api/foods`);
        const catRes = await axios.get(`${API_BASE_URL}/api/foods/categories`);
        setFoods(foodRes.data);
        setCategories(catRes.data);
        setLoading(false);
      } catch (err) {
        console.error("Backend not connected");
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Toast Timer
  useEffect(() => {
    if (addMsg) {
      const timer = setTimeout(() => setAddMsg(''), 2000);
      return () => clearTimeout(timer);
    }
  }, [addMsg]);

  const filteredFoods = activeCategory === 'All' 
    ? foods 
    : foods.filter(item => item.category?.name === activeCategory);

  const isInCart = (id) => cart.some(item => item._id === id);

  if (loading) return <div style={loaderStyle}>Loading Menu...</div>;

  return (
    <div style={containerStyle}>
      
      {/* --- NAVBAR --- */}
      <nav style={navStyle}>
        <div style={navContent}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
            <UtensilsCrossed size={isMobile ? 24 : 28} color="#e67e22" />
            <h2 style={{ margin: 0, fontSize: isMobile ? '1.2rem' : '1.5rem', letterSpacing: '-1px' }}>SKY BITES</h2>
          </div>
          
          <div style={{ display: 'flex', gap: isMobile ? '15px' : '20px', alignItems: 'center' }}>
            <Link to="/order" style={navLinkStyle}>
              <ClipboardList size={isMobile ? 22 : 20} />
              {!isMobile && <span>Orders</span>}
            </Link>
            <Link to="/cart" style={cartLinkStyle}>
              <ShoppingCart size={isMobile ? 20 : 18} />
              {!isMobile && <span>Cart</span>}
              {cart.length > 0 && <span style={badgeStyle}>{cart.length}</span>}
            </Link>
          </div>
        </div>
      </nav>

      {/* --- HEADER (Compact on Mobile) --- */}
      <header style={{...heroStyle, padding: isMobile ? '20px 10px' : '60px 20px'}}>
        <h1 style={{ fontSize: isMobile ? '1.8rem' : '3rem', margin: '0 0 5px 0', fontWeight: '900' }}>
          Hungry?
        </h1>
        <p style={{ fontSize: isMobile ? '0.9rem' : '1.1rem', opacity: 0.8, margin: 0 }}>
          Order fresh food instantly.
        </p>
      </header>

      {/* --- TOAST MESSAGE --- */}
      {addMsg && <div style={toastStyle}>{addMsg}</div>}

      {/* --- CATEGORIES (Pills) --- */}
      <div style={categoryContainer}>
        <button 
          onClick={() => setActiveCategory('All')}
          style={catButtonStyle(activeCategory === 'All', isMobile)}
        >
          All
        </button>
        {categories.map(cat => (
          <button 
            key={cat._id} 
            onClick={() => setActiveCategory(cat.name)}
            style={catButtonStyle(activeCategory === cat.name, isMobile)}
          >
            {cat.name}
          </button>
        ))}
      </div>

      {/* --- DUAL COLUMN GRID FOR PHONES --- */}
      <main style={{
        ...gridStyle,
        // If mobile, force 2 columns (1fr 1fr). If desktop, auto-fit.
        gridTemplateColumns: isMobile ? '1fr 1fr' : 'repeat(auto-fill, minmax(250px, 1fr))',
        gap: isMobile ? '10px' : '30px',
        padding: isMobile ? '0 10px 80px 10px' : '0 20px 100px 20px'
      }}>
        {filteredFoods.map(item => (
          <div key={item._id} style={cardStyle}>
            
            {/* Click Image to see details */}
            <div onClick={() => navigate(`/item/${item._id}`)} style={{ position: 'relative', cursor: 'pointer' }}>
              <img 
                src={item.image.startsWith('http') ? item.image : `${API_BASE_URL}/${item.image}`} 
                alt={item.name}
                onError={(e) => { e.target.src = 'https://via.placeholder.com/300?text=Yum'; }}
                style={{
                  ...imageStyle,
                  height: isMobile ? '120px' : '200px' // Smaller images on phone
                }} 
              />
              {/* Floating Price Tag */}
              <div style={isMobile ? mobilePriceBadge : desktopPriceBadge}>
                ${item.price}
              </div>
            </div>

            {/* Card Content */}
            <div style={{ padding: isMobile ? '10px' : '15px', display: 'flex', flexDirection: 'column', flex: 1 }}>
              <h3 style={{ 
                margin: '0 0 5px 0', 
                fontSize: isMobile ? '0.95rem' : '1.2rem',
                whiteSpace: 'nowrap', 
                overflow: 'hidden', 
                textOverflow: 'ellipsis' 
              }}>
                {item.name}
              </h3>
              
              {/* Hide Description on Mobile to save space */}
              {!isMobile && (
                <p style={{ fontSize: '0.85rem', color: '#666', margin: '0 0 10px 0' }}>
                  {item.description?.substring(0, 50)}...
                </p>
              )}

              {/* Add Button */}
              <div style={{ marginTop: 'auto' }}>
                {isInCart(item._id) ? (
                  <button onClick={() => removeFromCart(item._id)} style={removeBtnStyle(isMobile)}>
                    Remove
                  </button>
                ) : (
                  <button onClick={() => {
                    addToCart(item);
                    setAddMsg('Added!');
                  }} style={addBtnStyle(isMobile)}>
                    Add
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </main>

      {/* --- FOOTER --- */}
      <footer style={footerStyle}>
        <p>Â© 2026 Sky Bites</p>
        <Link to="/admin-panel" style={adminLinkStyle}>Admin Access</Link>
      </footer>
    </div>
  );
};

// --- STYLES ---

const containerStyle = {
  backgroundColor: '#f8f9fa',
  minHeight: '100vh',
  fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
  color: '#2d3436'
};

const navStyle = {
  position: 'sticky', top: 0, zIndex: 100, backgroundColor: 'rgba(255,255,255,0.95)',
