import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddItem = () => {
  const [categories, setCategories] = useState([]);
  const [foodData, setFoodData] = useState({ name: '', price: '', description: '', category: '' });
  const [image, setImage] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/api/foods/categories').then(res => setCategories(res.data));
  }, []);

  const handleAddFood = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('adminToken');
    const formData = new FormData();
    formData.append('name', foodData.name);
    formData.append('price', foodData.price);
    formData.append('description', foodData.description);
    formData.append('category', foodData.category);
    formData.append('image', image);

    try {
      await axios.post('http://localhost:5000/api/foods/add', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert("Food Added!");
      navigate('/admin-panel');
    } catch (err) {
      alert("Failed to add food item.");
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: '60px auto', padding: 24, borderRadius: 12, boxShadow: '0 2px 12px #eee', background: '#fff' }}>
      <h2>Add Food Item</h2>
      <form onSubmit={handleAddFood} style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
        <input
          placeholder="Food Name"
          value={foodData.name}
          onChange={e => setFoodData({ ...foodData, name: e.target.value })}
          required
          style={inputStyle}
        />
        <input
          placeholder="Price"
          type="number"
          value={foodData.price}
          onChange={e => setFoodData({ ...foodData, price: e.target.value })}
          required
          style={inputStyle}
        />
        <textarea
          placeholder="Description"
          value={foodData.description}
          onChange={e => setFoodData({ ...foodData, description: e.target.value })}
          style={inputStyle}
        />
        <select
          value={foodData.category}
          onChange={e => setFoodData({ ...foodData, category: e.target.value })}
          required
          style={inputStyle}
        >
          <option value="">Select Category</option>
          {categories.map(c => <option key={c._id} value={c._id}>{c.name}</option>)}
        </select>
        <input
          type="file"
          onChange={e => setImage(e.target.files[0])}
          required
          style={inputStyle}
        />
        <button type="submit" style={{ ...btnStyle, background: '#222' }}>Upload Item</button>
        <button type="button" style={{ ...btnStyle, background: '#aaa', color: '#222' }} onClick={() => navigate('/admin-panel')}>Back</button>
      </form>
    </div>
  );
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  borderRadius: '6px',
  border: '1px solid #ccc'
};

const btnStyle = {
  width: '100%',
  padding: '12px',
  margin: '8px 0',
  border: 'none',
  borderRadius: '6px',
  color: '#fff',
  fontSize: '16px',
  cursor: 'pointer',
  fontWeight: 'bold'
};

export default AddItem;
