import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../CartContext';
import { ShoppingCart, ClipboardList, UtensilsCrossed, Search } from 'lucide-react';

const Dashboard = () => {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [activeCategory, setActiveCategory] = useState('All');
  const [loading, setLoading] = useState(true);
  const [addMsg, setAddMsg] = useState('');
  
  // Responsive State
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  const { cart, addToCart, removeFromCart } = useCart();
  const navigate = useNavigate();
  const API_BASE_URL = "http://localhost:5000"; 

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    
    const fetchData = async () => {
      try {
        const foodRes = await axios.get(`${API_BASE_URL}/api/foods`);
        const catRes = await axios.get(`${API_BASE_URL}/api/foods/categories`);
        setFoods(foodRes.data);
        setCategories(catRes.data);
        setLoading(false);
      } catch (err) {
        setLoading(false);
      }
    };
    fetchData();
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const filteredFoods = activeCategory === 'All' 
    ? foods 
    : foods.filter(item => item.category?.name === activeCategory);

  const isInCart = (id) => cart.some(item => item._id === id);

  if (loading) return <div style={loaderStyle}>Sky Bites Loading...</div>;

  return (
    <div style={containerStyle}>
      
      {/* --- MOBILE OPTIMIZED NAVBAR --- */}
      <nav style={navStyle}>
        <div style={navContent}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
            <UtensilsCrossed size={isMobile ? 20 : 28} color="#e67e22" />
            <h2 style={{ margin: 0, fontSize: isMobile ? '1.2rem' : '1.8rem', letterSpacing: '-1px' }}>SKY BITES</h2>
          </div>
          
          <div style={{ display: 'flex', gap: isMobile ? '10px' : '20px', alignItems: 'center' }}>
            <Link to="/order" style={isMobile ? iconOnlyBtn : navLinkStyle}>
              <ClipboardList size={22} />
              {!isMobile && <span>Orders</span>}
            </Link>
            <Link to="/cart" style={isMobile ? cartCircleBtn : cartLinkStyle}>
              <ShoppingCart size={22} />
              {cart.length > 0 && <span style={badgeStyle}>{cart.length}</span>}
            </Link>
          </div>
        </div>
      </nav>

      {/* --- HERO SECTION (Smaller on Mobile) --- */}
      <header style={{...heroStyle, padding: isMobile ? '30px 15px' : '60px 20px'}}>
        <h1 style={{ fontSize: isMobile ? '1.8rem' : '3rem', margin: '0 0 8px 0', fontWeight: '900' }}>Quick Cravings?</h1>
        <p style={{ fontSize: isMobile ? '0.9rem' : '1.1rem', opacity: 0.8 }}>Delicious meals, delivered fast.</p>
      </header>

      {/* --- TOAST NOTIFICATION --- */}
      {addMsg && <div style={toastStyle}>{addMsg}</div>}

      {/* --- HORIZONTAL CATEGORY SCROLL --- */}
      <div style={categoryContainer}>
        <button 
          onClick={() => setActiveCategory('All')}
          style={catButtonStyle(activeCategory === 'All', isMobile)}
        >
          All
        </button>
        {categories.map(cat => (
          <button 
            key={cat._id} 
            onClick={() => setActiveCategory(cat.name)}
            style={catButtonStyle(activeCategory === cat.name, isMobile)}
          >
            {cat.name}
          </button>
        ))}
      </div>

      {/* --- DUAL COLUMN GRID FOR MOBILE --- */}
      <main style={{
        ...gridStyle,
        gridTemplateColumns: isMobile ? '1fr 1fr' : 'repeat(auto-fill, minmax(280px, 1fr))',
        gap: isMobile ? '12px' : '30px',
        padding: isMobile ? '0 12px' : '0 20px'
      }}>
        {filteredFoods.map(item => (
          <div key={item._id} style={isMobile ? mobileCardStyle : cardStyle}>
            <div onClick={() => navigate(`/item/${item._id}`)} style={{ position: 'relative' }}>
              <img 
                src={item.image.startsWith('http') ? item.image : `${API_BASE_URL}/${item.image}`} 
                alt={item.name}
                onError={(e) => { e.target.src = 'https://via.placeholder.com/200?text=Food'; }}
                style={{...imageStyle, height: isMobile ? '140px' : '220px'}} 
              />
              <div style={isMobile ? mobilePriceBadge : priceBadge}>${item.price}</div>
            </div>

            <div style={{ padding: isMobile ? '10px' : '20px', flex: 1 }}>
              <h3 style={{ margin: '0 0 4px 0', fontSize: isMobile ? '1rem' : '1.4rem', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                {item.name}
              </h3>
              {!isMobile && <p style={descStyle}>{item.description}</p>}
            </div>

            <div style={{ padding: isMobile ? '0 10px 10px' : '0 20px 20px' }}>
              <button 
                onClick={(e) => {
                  e.stopPropagation();
                  isInCart(item._id) ? removeFromCart(item._id) : addToCart(item);
                  if(!isInCart(item._id)) setAddMsg(`${item.name} added!`);
                }} 
                style={isInCart(item._id) ? removeBtnStyle : addBtnStyle(isMobile)}
              >
                {isInCart(item._id) ? 'Remove' : '+ Add'}
              </button>
            </div>
          </div>
        ))}
      </main>

      <footer style={footerStyle}>
        <p>Â© 2026 Sky Bites</p>
        <Link to="/admin-panel" style={adminLinkStyle}>Admin Access</Link>
      </footer>
    </div>
  );
};

// --- RESPONSIVE STYLES ---

const containerStyle = { backgroundColor: '#f9f9f9', minHeight: '100vh', fontFamily: 'sans-serif' };

const navStyle = { position: 'sticky', top: 0, zIndex: 1000, backgroundColor: '#fff', boxShadow: '0 2px 10px rgba(0,0,0,0.05)', padding: '12px 0' };

const navContent = { maxWidth: '1200px', margin: '0 auto', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 15px' };

const navLinkStyle = { display: 'flex', alignItems: 'center', gap: '5px', textDecoration: 'none', color: '#333', fontWeight: 'bold' };

const iconOnlyBtn = { color: '#333', display: 'flex', alignItems: 'center' };

const cartLinkStyle = { ...navLinkStyle, backgroundColor: '#000', color: '#fff', padding: '8px 15px', borderRadius: '25px', position: 'relative' };

const cartCircleBtn = { backgroundColor: '#000', color: '#fff', width: '40px', height: '40px', borderRadius: '50%', display: 'flex', justifyContent: 'center', alignItems: 'center', position: 'relative', textDecoration: 'none' };

const badgeStyle = { position: 'absolute', top: '-2px', right: '-2px', backgroundColor: '#e67e22', color: '#fff', fontSize: '10px', width: '18px', height: '18px', borderRadius: '50%', display: 'flex', justifyContent: 'center', alignItems: 'center', border: '2px solid #fff' };

const heroStyle = { textAlign: 'center', background: '#fff' };

const categoryContainer = { display: 'flex', gap: '8px', overflowX: 'auto', padding: '0 15px 20px', scrollbarWidth: 'none' };

const catButtonStyle = (isActive, isMobile) => ({
  padding: isMobile ? '8px 16px' : '12px 24px',
  borderRadius: '10px',
  border: 'none',
  backgroundColor: isActive ? '#e67e22' : '#eee',
  color: isActive ? '#fff' : '#333',
  fontWeight: 'bold',
  whiteSpace: 'nowrap',
  fontSize: isMobile ? '13px' : '15px'
