import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../CartContext';

const Dashboard = () => {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [activeCategory, setActiveCategory] = useState('All');
  const [loading, setLoading] = useState(true);
  const [addMsg, setAddMsg] = useState('');
  const { cart, addToCart, removeFromCart } = useCart();
  const navigate = useNavigate();

  const API_BASE_URL = "http://localhost:5000"; 

  useEffect(() => {
    const fetchData = async () => {
      try {
        const foodRes = await axios.get(`${API_BASE_URL}/api/foods`);
        const catRes = await axios.get(`${API_BASE_URL}/api/foods/categories`);
        setFoods(foodRes.data);
        setCategories(catRes.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching data:", err);
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Show message for 2 seconds
  useEffect(() => {
    if (addMsg) {
      const timer = setTimeout(() => setAddMsg(''), 2000);
      return () => clearTimeout(timer);
    }
  }, [addMsg]);

  const filteredFoods = activeCategory === 'All' 
    ? foods 
    : foods.filter(item => item.category?.name === activeCategory);

  // Check if item is in cart
  const isInCart = (id) => cart.some(item => item._id === id);

  if (loading) return <div style={{ textAlign: 'center', marginTop: '50px' }}>Loading Menu...</div>;

  return (
    <div style={{ 
      padding: '20px', 
      minHeight: '100vh', 
      display: 'flex', 
      flexDirection: 'column',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      backgroundColor: '#fafafa'
    }}>
      {/* HEADER SECTION */}
      <header style={{ textAlign: 'center', marginBottom: '30px' }}>
        <h1 style={{ fontSize: '2.5rem', marginBottom: '10px', fontWeight: '800' }}>MY RESTAURANT</h1>
        <p style={{ color: '#666' }}>Delicious food delivered to your soul.</p>
      </header>

      {/* Show Add Message */}
      {addMsg && (
        <div style={{
          background: '#4ecca3',
          color: '#222',
          padding: '12px',
          borderRadius: '8px',
          textAlign: 'center',
          marginBottom: '18px',
          fontWeight: 'bold',
          fontSize: '16px',
          maxWidth: '400px',
          margin: '0 auto 18px auto'
        }}>
          {addMsg}
        </div>
      )}

      {/* CATEGORY FILTER SECTION */}
      <div style={{ 
        display: 'flex', 
        gap: '12px', 
        overflowX: 'auto', 
        paddingBottom: '20px',
        justifyContent: 'center',
        scrollbarWidth: 'none'
      }}>
        <button 
          onClick={() => setActiveCategory('All')}
          style={catButtonStyle(activeCategory === 'All')}
        >
          All Items
        </button>
        {categories.map(cat => (
          <button 
            key={cat._id} 
            onClick={() => setActiveCategory(cat.name)}
            style={catButtonStyle(activeCategory === cat.name)}
          >
            {cat.name}
          </button>
        ))}
      </div>

      {/* FOOD GRID SECTION */}
      <div style={{ 
        flex: 1,
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))', 
        gap: '25px',
        maxWidth: '1200px',
        margin: '0 auto',
        width: '100%'
      }}>
        {filteredFoods.length > 0 ? filteredFoods.map(item => (
          <div key={item._id} style={cardStyle}>
            <div
              style={{ cursor: 'pointer' }}
              onClick={() => navigate(`/item/${item._id}`)}
            >
              <img 
                src={item.image.startsWith('http') ? item.image : `${API_BASE_URL}/${item.image}`} 
                alt={item.name}
                onError={(e) => { e.target.src = 'https://via.placeholder.com/300x200?text=Food+Image'; }}
                style={imageStyle} 
              />
              <div style={{ padding: '15px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'baseline' }}>
                  <h3 style={{ margin: 0, fontSize: '1.2rem' }}>{item.name}</h3>
                  <span style={{ color: '#e67e22', fontWeight: 'bold' }}>${item.price}</span>
                </div>
                <p style={{ color: '#777', fontSize: '0.9rem', marginTop: '8px', lineHeight: '1.4' }}>
                  {item.description || "No description available."}
                </p>
              </div>
            </div>
            {isInCart(item._id) ? (
              <button
                onClick={() => removeFromCart(item._id)}
                style={{
                  width: '100%',
                  padding: '10px',
                  background: '#e74c3c',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '0 0 16px 16px',
                  fontWeight: 'bold',
                  cursor: 'pointer'
                }}
              >
                Remove from Cart
              </button>
            ) : (
              <button
                onClick={() => {
                  addToCart(item);
                  setAddMsg(`"${item.name}" added to cart!`);
                }}
                style={{
                  width: '100%',
                  padding: '10px',
                  background: '#222',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '0 0 16px 16px',
                  fontWeight: 'bold',
                  cursor: 'pointer'
                }}
              >
                Add to Cart
              </button>
            )}
          </div>
        )) : <p style={{ textAlign: 'center', gridColumn: '1/-1' }}>No items found in this category.</p>}
      </div>

      {/* ENHANCED FOOTER SECTION */}
      <footer style={{ 
        marginTop: '50px', 
        padding: '30px 20px', 
        textAlign: 'center', 
        borderTop: '1px solid #eee',
        color: '#888'
      }}>
        <p style={{ fontSize: '14px', margin: '0 0 10px 0' }}>Â© 2026 My Restaurant App</p>
        
        <Link 
          to="/cart" 
          style={{ 
            color: '#222', 
            textDecoration: 'underline', 
            fontSize: '14px',
            marginRight: 20
          }}
        >
          View Cart
        </Link>

        <Link 
          to="/order"
          style={{ 
            color: '#222', 
            textDecoration: 'underline', 
            fontSize: '14px',
            marginRight: 20
          }}
        >
          Order
        </Link>

        <Link 
          to="/admin-panel" 
          style={{ 
            color: '#ddd', 
            textDecoration: 'none', 
            fontSize: '11px',
            transition: 'color 0.3s'
          }}
          onMouseEnter={(e) => e.target.style.color = '#888'}
          onMouseLeave={(e) => e.target.style.color = '#ddd'}
        >
          Admin Access
        </Link>
      </footer>
    </div>
  );
};

// --- STYLING HELPERS ---

const catButtonStyle = (isActive) => ({
  padding: '10px 22px',
  borderRadius: '50px',
  border: 'none',
  backgroundColor: isActive ? '#000' : '#fff',
  color: isActive ? '#fff' : '#555',
  cursor: 'pointer',
  fontWeight: '600',
  boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
  transition: 'all 0.2s ease',
  whiteSpace: 'nowrap'
});

const cardStyle = {
  backgroundColor: '#fff',
  borderRadius: '16px',
  overflow: 'hidden',
  boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
  transition: 'transform 0.3s ease',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between'
};

const imageStyle = { 
  width: '100%', 
  height: '200px', 
  objectFit: 'cover' 
};

export default Dashboard;
