import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../CartContext';

const API_BASE_URL = "http://localhost:5000";

const ItemDetail = () => {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [size, setSize] = useState('Full'); // State to track Half/Full
  const [added, setAdded] = useState(false);
  const { addToCart } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`${API_BASE_URL}/api/foods`)
      .then(res => {
        const found = res.data.find(f => f._id === id);
        setItem(found);
      });
  }, [id]);

  if (!item) return <div style={{ textAlign: 'center', marginTop: 50 }}>Loading...</div>;

  // 1. Logic to calculate price based on size
  // Note: Standard half plate is usually 50-60% of full price. 
  // Here I am using 50% (Math.ceil rounds up to nearest Rupee)
  const currentPrice = size === 'Half' ? Math.ceil(item.price / 2) : item.price;

  const handleAddToCart = () => {
    // 2. Send the modified item to cart with specific size and price
    addToCart({
      ...item,
      _id: item._id + size, // Create unique ID (e.g., "123Half") so they don't merge in cart
      originalId: item._id, // Keep reference to original DB ID
      name: `${item.name} (${size})`, // Update name to show size (e.g. "Dal Makhani (Half)")
      price: currentPrice, // Use the calculated price
      selectedSize: size
    });
    setAdded(true);
    
    // Reset "Added" button after 1 second so they can add more
    setTimeout(() => setAdded(false), 1000);
  };

  return (
    <div style={{ maxWidth: 600, margin: '40px auto', background: '#fff', borderRadius: 12, boxShadow: '0 2px 12px #eee', padding: 24 }}>
      <img
        src={item.image.startsWith('http') ? item.image : `${API_BASE_URL}/${item.image}`}
        alt={item.name}
        style={{ width: '100%', height: 300, objectFit: 'cover', borderRadius: 10, marginBottom: 24 }}
      />
      
      <h2>{item.name}</h2>
      
      {/* 3. Display Dynamic Price in Rupee */}
      <p style={{ color: '#e67e22', fontWeight: 'bold', fontSize: 24 }}>
        ₹{currentPrice}
      </p>

      <p>{item.description}</p>
      <p style={{ color: '#888', fontSize: 14 }}>Category: {item.category?.name || 'N/A'}</p>

      {/* 4. Portion Selector (Half / Full) */}
      <div style={{ margin: '20px 0', display: 'flex', gap: '10px', alignItems: 'center' }}>
        <span style={{ fontWeight: 'bold' }}>Portion:</span>
        <button 
          onClick={() => setSize('Half')}
          style={size === 'Half' ? activeSizeBtn : inactiveSizeBtn}
        >
          Half
        </button>
        <button 
          onClick={() => setSize('Full')}
          style={size === 'Full' ? activeSizeBtn : inactiveSizeBtn}
        >
          Full
        </button>
      </div>

      <div style={{ borderTop: '1px solid #eee', paddingTop: 20, marginTop: 20 }}>
        <button
          onClick={handleAddToCart}
          style={{
            padding: '12px 30px',
            background: added ? '#27ae60' : '#222', // Green if added
            color: '#fff',
            border: 'none',
            borderRadius: 6,
            fontWeight: 'bold',
            cursor: 'pointer',
            transition: '0.3s'
          }}
        >
          {added ? "✔ Added" : `Add to Thali (₹${currentPrice})`}
        </button>
        
        <button
          style={{
            marginLeft: 16,
            padding: '12px 30px',
            background: '#eee',
            color: '#222',
            border: 'none',
            borderRadius: 6,
            fontWeight: 'bold',
            cursor: 'pointer'
          }}
          onClick={() => navigate('/')}
        >
          Back to Menu
        </button>
      </div>
    </div>
  );
};

// --- Styles for the Half/Full Buttons ---
const baseSizeBtn = {
  padding: '8px 20px',
  borderRadius: '20px',
  cursor: 'pointer',
  fontWeight: 'bold',
  fontSize: '14px',
  border: '1px solid #ddd'
};

const activeSizeBtn = {
  ...baseSizeBtn,
  background: '#e67e22',
  color: '#fff',
  border: '1px solid #e67e22'
};

const inactiveSizeBtn = {
  ...baseSizeBtn,
  background: '#fff',
  color: '#555'
};

export default ItemDetail;
