import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SearchEditItem = () => {
  const [search, setSearch] = useState('');
  const [foods, setFoods] = useState([]);
  const [editing, setEditing] = useState(null);
  const [editData, setEditData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/api/foods').then(res => setFoods(res.data));
  }, []);

  const filteredFoods = foods.filter(f => f.name.toLowerCase().includes(search.toLowerCase()));

  const startEdit = (food) => {
    setEditing(food._id);
    setEditData({ name: food.name, price: food.price, description: food.description });
  };

  const handleEdit = async (id) => {
    const token = localStorage.getItem('adminToken');
    try {
      await axios.put(`http://localhost:5000/api/foods/${id}`, editData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert("Item updated!");
      setEditing(null);
      // Refresh foods
      const res = await axios.get('http://localhost:5000/api/foods');
      setFoods(res.data);
    } catch (err) {
      alert("Failed to update item.");
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: '60px auto', padding: 24, borderRadius: 12, boxShadow: '0 2px 12px #eee', background: '#fff' }}>
      <h2>Search & Edit Food Item</h2>
      <input
        placeholder="Search by name..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ width: '100%', padding: 10, borderRadius: 6, border: '1px solid #ccc', marginBottom: 20 }}
      />
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {filteredFoods.map(food => (
          <li key={food._id} style={{ marginBottom: 16, borderBottom: '1px solid #eee', paddingBottom: 10 }}>
            {editing === food._id ? (
              <div>
                <input value={editData.name} onChange={e => setEditData({ ...editData, name: e.target.value })} style={{ marginRight: 8 }} />
                <input value={editData.price} type="number" onChange={e => setEditData({ ...editData, price: e.target.value })} style={{ marginRight: 8 }} />
                <input value={editData.description} onChange={e => setEditData({ ...editData, description: e.target.value })} style={{ marginRight: 8 }} />
                <button onClick={() => handleEdit(food._id)} style={{ ...btnStyle, background: '#222' }}>Save</button>
                <button onClick={() => setEditing(null)} style={{ ...btnStyle, background: '#aaa', color: '#222' }}>Cancel</button>
              </div>
            ) : (
              <div>
                <strong>{food.name}</strong> (${food.price}) - {food.description}
                <button onClick={() => startEdit(food)} style={{ ...btnStyle, background: '#222', marginLeft: 12 }}>Edit</button>
              </div>
            )}
          </li>
        ))}
      </ul>
      <button style={{ ...btnStyle, background: '#aaa', color: '#222' }} onClick={() => navigate('/admin-panel')}>Back</button>
    </div>
  );
};

const btnStyle = {
  padding: '6px 14px',
  border: 'none',
  borderRadius: '6px',
  color: '#fff',
  fontSize: '14px',
  cursor: 'pointer',
  fontWeight: 'bold'
};

export default SearchEditItem;
