import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SearchEditItem = () => {
  const [search, setSearch] = useState('');
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]); // State for categories
  const [editing, setEditing] = useState(null);
  const [editData, setEditData] = useState({
    name: '',
    halfPrice: '',
    fullPrice: '',
    description: '',
    category: ''
  });
  const [newImage, setNewImage] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch both foods and categories on mount
    const fetchData = async () => {
      try {
        const [foodRes, catRes] = await Promise.all([
          axios.get('http://localhost:5000/api/foods'),
          axios.get('http://localhost:5000/api/categories') // Adjust URL if different
        ]);
        setFoods(foodRes.data);
        setCategories(catRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };
    fetchData();
  }, []);

  const filteredFoods = foods.filter(f => f.name.toLowerCase().includes(search.toLowerCase()));

  const startEdit = (food) => {
    setEditing(food._id);
    setEditData({
      name: food.name,
      halfPrice: food.halfPrice || '',
      fullPrice: food.fullPrice || '',
      description: food.description || '',
      category: food.category?._id || food.category || '' // Handles populated or ID-only category
    });
    setNewImage(null);
  };

  const handleEdit = async (id) => {
    const token = localStorage.getItem('adminToken');
    try {
      const formData = new FormData();
      formData.append('name', editData.name);
      formData.append('halfPrice', editData.halfPrice);
      formData.append('fullPrice', editData.fullPrice);
      formData.append('description', editData.description);
      formData.append('category', editData.category);

      if (newImage) {
        formData.append('image', newImage);
      }

      await axios.put(`http://localhost:5000/api/foods/${id}`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      alert("Item updated!");
      setEditing(null);
      setNewImage(null);
      
      // Refresh food list
      const res = await axios.get('http://localhost:5000/api/foods');
      setFoods(res.data);
    } catch (err) {
      alert("Failed to update item.");
      console.error(err);
    }
  };

  return (
    <div style={{ maxWidth: 700, margin: '60px auto', padding: 24, borderRadius: 12, boxShadow: '0 2px 12px #eee', background: '#fff' }}>
      <h2>Search & Edit Food Item</h2>
      <input
        placeholder="Search by name..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ width: '100%', padding: 10, borderRadius: 6, border: '1px solid #ccc', marginBottom: 20 }}
      />
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {filteredFoods.map(food => (
          <li key={food._id} style={{ marginBottom: 16, borderBottom: '1px solid #eee', paddingBottom: 10 }}>
            {editing === food._id ? (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                <input 
                  value={editData.name} 
                  onChange={e => setEditData({ ...editData, name: e.target.value })} 
                  placeholder="Name"
                  style={inputStyle} 
                />
                
                <div style={{ display: 'flex', gap: '10px' }}>
                    <input 
                      value={editData.halfPrice} 
                      type="number" 
                      onChange={e => setEditData({ ...editData, halfPrice: e.target.value })} 
                      placeholder="Half Price"
                      style={inputStyle} 
                    />
                    <input 
                      value={editData.fullPrice} 
                      type="number" 
                      onChange={e => setEditData({ ...editData, fullPrice: e.target.value })} 
                      placeholder="Full Price"
                      style={inputStyle} 
                    />
                </div>

                <input 
                  value={editData.description} 
                  onChange={e => setEditData({ ...editData, description: e.target.value })} 
                  placeholder="Description"
                  style={inputStyle} 
                />

                <select
                  value={editData.category}
                  onChange={e => setEditData({ ...editData, category: e.target.value })}
                  style={inputStyle}
                >
                  <option value="">Select Category</option>
                  {categories.map(c => <option key={c._id} value={c._id}>{c.name}</option>)}
                </select>
                
                <div style={{ marginBottom: 10 }}>
                  <label style={{ fontSize: '12px', fontWeight: 'bold', color: '#666', display: 'block', marginBottom: 5 }}>
                    Update Image (Optional)
                  </label>
                  <input 
                    type="file"
                    accept="image/*"
                    onChange={e => setNewImage(e.target.files[0])}
                    style={{ padding: 8, width: '100%' }}
                  />
                  {newImage && <p style={{ fontSize: '12px', color: '#27ae60', marginTop: 5 }}>✓ New image selected</p>}
                </div>

                <div>
                  <button onClick={() => handleEdit(food._id)} style={{ ...btnStyle, background: '#222', marginRight: 8 }}>Save</button>
                  <button onClick={() => { setEditing(null); setNewImage(null); }} style={{ ...btnStyle, background: '#aaa', color: '#222' }}>Cancel</button>
                </div>
              </div>
            ) : (
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <strong>{food.name}</strong> 
                  <span style={{ fontSize: '14px', color: '#555', marginLeft: 10 }}>
                    (Half: ₹{food.halfPrice} | Full: ₹{food.fullPrice})
                  </span>
                  <p style={{ margin: '4px 0', fontSize: '13px', color: '#777' }}>{food.description}</p>
                  <small style={{ color: '#007bff' }}>Category: {food.category?.name || 'Uncategorized'}</small>
                </div>
                <button onClick={() => startEdit(food)} style={{ ...btnStyle, background: '#222' }}>Edit</button>
              </div>
            )}
          </li>
        ))}
      </ul>
      <button style={{ ...btnStyle, background: '#aaa', color: '#222', marginTop: 20 }} onClick={() => navigate('/admin-panel')}>Back</button>
    </div>
  );
};

const inputStyle = {
  padding: '8px',
  width: '100%',
  borderRadius: '6px',
  border: '1px solid #ccc',
  boxSizing: 'border-box'
};

const btnStyle = {
  padding: '6px 14px',
  border: 'none',
  borderRadius: '6px',
  color: '#fff',
  fontSize: '14px',
  cursor: 'pointer',
  fontWeight: 'bold'
};

export default SearchEditItem;
