import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Search, Edit3, Save, X, ArrowLeft, Trash2 } from 'lucide-react';

const SearchEditItem = () => {
  const [search, setSearch] = useState('');
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [editing, setEditing] = useState(null);
  const [editData, setEditData] = useState({
    name: '',
    halfPrice: '',
    fullPrice: '',
    description: '',
    category: ''
  });
  const [newImage, setNewImage] = useState(null);
  const navigate = useNavigate();

  const API_BASE_URL = "http://localhost:5000";

  useEffect(() => {
    axios.get(`${API_BASE_URL}/api/foods`).then(res => setFoods(res.data));
    axios.get(`${API_BASE_URL}/api/foods/categories`).then(res => setCategories(res.data));
  }, []);

  const filteredFoods = foods.filter(f => f.name.toLowerCase().includes(search.toLowerCase()));

  // --- DELETE LOGIC ---
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    try {
      const token = localStorage.getItem('adminToken');
      await axios.delete(`${API_BASE_URL}/api/foods/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setFoods(foods.filter(f => f._id !== id));
    } catch (err) {
      alert("Failed to delete item.");
    }
  };

  const startEdit = (food) => {
    setEditing(food._id);
    setEditData({
      name: food.name,
      halfPrice: food.halfPrice || '',
      fullPrice: food.fullPrice || '',
      description: food.description || '',
      category: food.category?._id || ''
    });
    setNewImage(null);
  };

  const handleEdit = async (id) => {
    const token = localStorage.getItem('adminToken');
    try {
      const formData = new FormData();
      formData.append('name', editData.name);
      formData.append('halfPrice', editData.halfPrice);
      formData.append('fullPrice', editData.fullPrice);
      formData.append('description', editData.description);
      formData.append('category', editData.category);
      if (newImage) formData.append('image', newImage);

      await axios.put(`${API_BASE_URL}/api/foods/${id}`, formData, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert("Item updated successfully!");
      setEditing(null);
      const res = await axios.get(`${API_BASE_URL}/api/foods`);
      setFoods(res.data);
    } catch (err) {
      alert("Failed to update item.");
    }
  };

  return (
    <div style={container}>
      {/* Header Area */}
      <div style={header}>
        <button onClick={() => navigate('/admin-panel')} style={backCircleBtn}>
          <ArrowLeft size={20} />
        </button>
        <h2 style={{ margin: 0, fontSize: '22px' }}>Manage Menu Items</h2>
      </div>

      {/* Search Input */}
      <div style={searchWrapper}>
        <Search size={18} style={searchIcon} />
        <input
          placeholder="Search your dishes..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={searchInput}
        />
      </div>

      {/* Item List */}
      <div style={listWrapper}>
        {filteredFoods.map(food => (
          <div key={food._id} style={itemCard}>
            {editing === food._id ? (
              <div style={editForm}>
                <h4 style={{ marginBottom: 15, color: '#e67e22' }}>Editing: {food.name}</h4>
                
                <label style={label}>Item Name</label>
                <input style={input} value={editData.name} onChange={e => setEditData({ ...editData, name: e.target.value })} />
                
                <div style={row}>
                   <div style={{ flex: 1 }}>
                     <label style={label}>Half Price</label>
                     <input style={input} type="number" value={editData.halfPrice} onChange={e => setEditData({ ...editData, halfPrice: e.target.value })} />
                   </div>
                   <div style={{ flex: 1 }}>
                     <label style={label}>Full Price</label>
                     <input style={input} type="number" value={editData.fullPrice} onChange={e => setEditData({ ...editData, fullPrice: e.target.value })} />
                   </div>
                </div>

                <label style={label}>Category</label>
                <select style={input} value={editData.category} onChange={e => setEditData({ ...editData, category: e.target.value })}>
                  <option value="">Select Category</option>
                  {categories.map(c => <option key={c._id} value={c._id}>{c.name}</option>)}
                </select>

                <label style={label}>Description</label>
                <textarea style={{ ...input, height: '60px' }} value={editData.description} onChange={e => setEditData({ ...editData, description: e.target.value })} />

                <label style={label}>Change Image</label>
                <input type="file" accept="image/*" onChange={e => setNewImage(e.target.files[0])} style={{ marginBottom: 15 }} />

                <div style={actionRow}>
                  <button onClick={() => handleEdit(food._id)} style={saveBtn}><Save size={16} /> Save Changes</button>
                  <button onClick={() => setEditing(null)} style={cancelBtn}><X size={16} /> Cancel</button>
                </div>
              </div>
            ) : (
              <div style={displayRow}>
                <img 
                  src={food.image?.startsWith('http') ? food.image : `${API_BASE_URL}/${food.image}`} 
                  alt={food.name} 
                  style={thumb}
                  onError={(e) => { e.target.src = 'https://via.placeholder.com/60'; }}
                />
                <div style={info}>
                  <h4 style={{ margin: 0 }}>{food.name}</h4>
                  <p style={priceText}>₹{food.fullPrice} {food.halfPrice && `| ₹${food.halfPrice}`}</p>
                  <p style={descText}>{food.description}</p>
                </div>
                <div style={{ display: 'flex', gap: '8px' }}>
                  <button onClick={() => startEdit(food)} style={editIconBtn} title="Edit">
                    <Edit3 size={18} />
                  </button>
                  <button onClick={() => handleDelete(food._id)} style={{ ...editIconBtn, color: '#e74c3c' }} title="Delete">
                    <Trash2 size={18} />
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

