import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../CartContext';

const API_BASE_URL = "http://localhost:5000";

const ItemDetail = () => {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [added, setAdded] = useState(false);
  const { addToCart } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`${API_BASE_URL}/api/foods`)
      .then(res => {
        const found = res.data.find(f => f._id === id);
        setItem(found);
      });
  }, [id]);

  if (!item) return <div style={{ textAlign: 'center', marginTop: 50 }}>Loading...</div>;

  const handleAddToCart = () => {
    addToCart(item);
    setAdded(true);
  };

  return (
    <div style={{ maxWidth: 600, margin: '40px auto', background: '#fff', borderRadius: 12, boxShadow: '0 2px 12px #eee', padding: 24 }}>
      <img
        src={item.image.startsWith('http') ? item.image : `${API_BASE_URL}/${item.image}`}
        alt={item.name}
        style={{ width: '100%', height: 300, objectFit: 'cover', borderRadius: 10, marginBottom: 24 }}
      />
      <h2>{item.name}</h2>
      <p style={{ color: '#e67e22', fontWeight: 'bold', fontSize: 20 }}>${item.price}</p>
      <p>{item.description}</p>
      <p style={{ color: '#888', fontSize: 14 }}>Category: {item.category?.name || 'N/A'}</p>
      <button
        onClick={handleAddToCart}
        style={{
          marginTop: 20,
          padding: '12px 30px',
          background: added ? '#aaa' : '#222',
          color: '#fff',
          border: 'none',
          borderRadius: 6,
          fontWeight: 'bold',
          cursor: added ? 'not-allowed' : 'pointer'
        }}
        disabled={added}
      >
        {added ? "Added to Cart" : "Add to Cart"}
      </button>
      <button
        style={{
          marginLeft: 16,
          padding: '12px 30px',
          background: '#eee',
          color: '#222',
          border: 'none',
          borderRadius: 6,
          fontWeight: 'bold',
          cursor: 'pointer'
        }}
        onClick={() => navigate('/')}
      >
        Back to Menu
      </button>
    </div>
  );
};

export default ItemDetail;
