import React, { useState } from 'react';
import { useCart } from '../CartContext';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const { cart, clearCart, addToCart, decreaseQuantity, removeFromCart } = useCart();
  const [ordered, setOrdered] = useState(false);
  const navigate = useNavigate();

  const handleOrder = () => {
    clearCart();
    setOrdered(true);
  };

  const totalAmount = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  if (ordered) {
    return (
      <div style={{ textAlign: 'center', marginTop: 80 }}>
        <h2>Order Placed Successfully!</h2>
        <button onClick={() => navigate('/')} style={btnStyle}>Back to Menu</button>
      </div>
    );
  }

  return (
    <div style={{ maxWidth: 600, margin: '40px auto', background: '#fff', borderRadius: 12, boxShadow: '0 2px 12px #eee', padding: 24 }}>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>No items in cart.</p>
      ) : (
        <>
          <ul style={{ padding: 0 }}>
            {cart.map((item, idx) => (
              <li key={idx} style={{ marginBottom: 12, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <span>
                  <strong>{item.name}</strong> - ${item.price} x {item.quantity} = ${item.price * item.quantity}
                </span>
                <div>
                  <button
                    onClick={() => decreaseQuantity(item._id)}
                    style={qtyBtn}
                  >-</button>
                  <span style={{ margin: '0 8px' }}>{item.quantity}</span>
                  <button
                    onClick={() => addToCart(item)}
                    style={qtyBtn}
                  >+</button>
                  <button
                    onClick={() => removeFromCart(item._id)}
                    style={{
                      background: '#e74c3c',
                      color: '#fff',
                      border: 'none',
                      borderRadius: 6,
                      padding: '6px 14px',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      marginLeft: 12
                    }}
                  >
                    Remove
                  </button>
                </div>
              </li>
            ))}
          </ul>
          <div style={{ fontWeight: 'bold', fontSize: 18, marginTop: 18 }}>
            Total Amount: ${totalAmount}
          </div>
          <button onClick={handleOrder} style={btnStyle}>Place Order</button>
        </>
      )}
      <button onClick={() => navigate('/')} style={{ ...btnStyle, background: '#eee', color: '#222', marginTop: 12 }}>Back to Menu</button>
    </div>
  );
};

const btnStyle = {
  padding: '12px 30px',
  background: '#222',
  color: '#fff',
  border: 'none',
  borderRadius: 6,
  fontWeight: 'bold',
  cursor: 'pointer',
  marginTop: 20
};

const qtyBtn = {
  background: '#222',
  color: '#fff',
  border: 'none',
  borderRadius: '50%',
  width: 28,
  height: 28,
  fontWeight: 'bold',
  fontSize: 18,
  cursor: 'pointer'
};

export default Cart;
