import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SearchEditItem = () => {
  const [search, setSearch] = useState('');
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [editing, setEditing] = useState(null);
  const [editData, setEditData] = useState({
    name: '',
    halfPrice: '',
    fullPrice: '',
    description: '',
    category: ''
  });
  const [newImage, setNewImage] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/api/foods').then(res => setFoods(res.data));
    axios.get('http://localhost:5000/api/foods/categories').then(res => setCategories(res.data));
  }, []);

  const filteredFoods = foods.filter(f => f.name.toLowerCase().includes(search.toLowerCase()));

  const startEdit = (food) => {
    setEditing(food._id);
    setEditData({
      name: food.name,
      halfPrice: food.halfPrice || '',
      fullPrice: food.fullPrice || '',
      description: food.description || '',
      category: food.category?._id || ''
    });
    setNewImage(null);
  };

  const handleEdit = async (id) => {
    const token = localStorage.getItem('adminToken');
    try {
      const formData = new FormData();
      formData.append('name', editData.name);
      formData.append('halfPrice', editData.halfPrice);
      formData.append('fullPrice', editData.fullPrice);
      formData.append('description', editData.description);
      formData.append('category', editData.category);
      if (newImage) {
        formData.append('image', newImage);
      }

      await axios.put(`http://localhost:5000/api/foods/${id}`, formData, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert("Item updated!");
      setEditing(null);
      setNewImage(null);
      const res = await axios.get('http://localhost:5000/api/foods');
      setFoods(res.data);
    } catch (err) {
      alert("Failed to update item.");
      console.error(err);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: '60px auto', padding: 24, borderRadius: 12, boxShadow: '0 2px 12px #eee', background: '#fff' }}>
      <h2>Search & Edit Food Item</h2>
      <input
        placeholder="Search by name..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ width: '100%', padding: 10, borderRadius: 6, border: '1px solid #ccc', marginBottom: 20 }}
      />
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {filteredFoods.map(food => (
          <li key={food._id} style={{ marginBottom: 16, borderBottom: '1px solid #eee', paddingBottom: 10 }}>
            {editing === food._id ? (
              <div>
                <input 
                  value={editData.name} 
                  onChange={e => setEditData({ ...editData, name: e.target.value })} 
                  placeholder="Name"
                  style={{ marginRight: 8, padding: 8, width: '100%', marginBottom: 10 }} 
                />
                <input 
                  value={editData.halfPrice} 
                  type="number" 
                  onChange={e => setEditData({ ...editData, halfPrice: e.target.value })} 
                  placeholder="Half Price"
                  style={{ marginRight: 8, padding: 8, width: '100%', marginBottom: 10 }} 
                />
                <input 
                  value={editData.fullPrice} 
                  type="number" 
                  onChange={e => setEditData({ ...editData, fullPrice: e.target.value })} 
                  placeholder="Full Price"
                  style={{ marginRight: 8, padding: 8, width: '100%', marginBottom: 10 }} 
                />
                <input 
                  value={editData.description} 
                  onChange={e => setEditData({ ...editData, description: e.target.value })} 
                  placeholder="Description"
                  style={{ marginRight: 8, padding: 8, width: '100%', marginBottom: 10 }} 
                />
                <select
                  value={editData.category}
                  onChange={e => setEditData({ ...editData, category: e.target.value })}
                  style={{ marginRight: 8, padding: 8, width: '100%', marginBottom: 10 }}
                >
                  <option value="">Select Category</option>
                  {categories.map(c => <option key={c._id} value={c._id}>{c.name}</option>)}
                </select>
                <div style={{ marginBottom: 10 }}>
                  <label style={{ fontSize: '12px', fontWeight: 'bold', color: '#666', display: 'block', marginBottom: 5 }}>
                    Update Image (Optional)
                  </label>
                  <input 
                    type="file"
                    accept="image/*"
                    onChange={e => setNewImage(e.target.files[0])}
                    style={{ padding: 8, width: '100%' }}
                  />
                  {newImage && <p style={{ fontSize: '12px', color: '#27ae60', marginTop: 5 }}>✓ New image selected</p>}
                </div>
                <button 
                  onClick={() => handleEdit(food._id)} 
                  style={{ ...btnStyle, background: '#222', marginRight: 8 }}
                >
                  Save
                </button>
                <button 
                  onClick={() => {
                    setEditing(null);
                    setNewImage(null);
                  }} 
                  style={{ ...btnStyle, background: '#aaa', color: '#222' }}
                >
                  Cancel
                </button>
              </div>
            ) : (
              <div>
                <strong>{food.name}</strong> (₹{food.halfPrice || food.fullPrice}) - {food.description}
                <button onClick={() => startEdit(food)} style={{ ...btnStyle, background: '#222', marginLeft: 12 }}>Edit</button>
              </div>
            )}
          </li>
        ))}
      </ul>
      <button style={{ ...btnStyle, background: '#aaa', color: '#222' }} onClick={() => navigate('/admin-panel')}>Back</button>
    </div>
  );
};

const btnStyle = {
  padding: '6px 14px',
  border: 'none',
  borderRadius: '6px',
  color: '#fff',
  fontSize: '14px',
  cursor: 'pointer',
  fontWeight: 'bold'
};

export default SearchEditItem;
